'use strict';

const ClientError = require('./ClientError');

class BadRequestError extends ClientError {
  constructor(options) {
    super(Object.assign(
      {
        name: 'BadRequestError',
        message: 'bad request'
      },
      options,
      {
        statusCode: BadRequestError.STATUS_CODE
      }
    ));
  }

  static get STATUS_CODE() {
    return 400;
  }
}

module.exports = BadRequestError;
