'use strict';

const LambdaError = require('./LambdaError');

class ClientError extends LambdaError {
  constructor(options) {
    super(Object.assign(
      {
        name: 'ClientError',
        statusCode: ClientError.STATUS_CODE
      },
      options
    ));
  }

  static get STATUS_CODE() {
    return 400;
  }
}

module.exports = ClientError;
