'use strict';

const ClientError = require('./ClientError');

class ConflictError extends ClientError {
  constructor(options) {
    super(Object.assign(
      {
        name: 'ConflictError',
        message: 'request conflict'
      },
      options,
      {
        statusCode: ConflictError.STATUS_CODE
      }
    ));
  }

  static get STATUS_CODE() {
    return 409;
  }
}

module.exports = ConflictError;
