'use strict';

const WError = require('verror').WError;

class LambdaError extends WError {
  constructor(options) {
    options = options || {}; // eslint-disable-line no-param-reassign

    super({
      name: options.name || 'LambdaError',
      info: options.info,
      cause: options.cause
    }, options.message || '');

    this.statusCode = options.statusCode || 500;
    this.details = options.details || null;
  }
}

// WError has prototype.name set, but it is inherited by all child classes.
// This may cause problems when using libraries like errio.
LambdaError.prototype.name = undefined;

module.exports = LambdaError;
