'use strict';

const ClientError = require('./ClientError');

class NotFoundError extends ClientError {
  constructor(options) {
    super(Object.assign(
      {
        name: 'NotFoundError',
        message: 'not found'
      },
      options,
      {
        statusCode: NotFoundError.STATUS_CODE
      }
    ));
  }

  static get STATUS_CODE() {
    return 404;
  }
}

module.exports = NotFoundError;
