'use strict';

const LambdaError = require('./LambdaError');

class ServerError extends LambdaError {
  constructor(options) {
    super(Object.assign(
      {
        name: 'ServerError',
        statusCode: ServerError.STATUS_CODE,
        message: 'internal server error'
      },
      options
    ));
  }

  static get STATUS_CODE() {
    return 500;
  }
}

module.exports = ServerError;
